CREATE TYPE category_type AS enum (
	'Pre-Move Services',
	'Physical Move Services',
	'Liability'
);

CREATE TYPE sub_category_type AS enum (
    'Customer Support',
    'Counseling',
    'Weight Estimate',
	'Additional Services',
	'Inventory & Documentation',
	'Packing/Unpacking',
	'Shipment Schedule',
	'Shipment Weights',
	'Storage',
	'Workforce/Sub-Contractor Management',
	'Loss & Damage',
	'Inconvenience & Hardship Claims'
);

CREATE TABLE IF NOT EXISTS pws_violations (
    id integer PRIMARY KEY,
    paragraph_number text,
    title text,
    category text,
    sub_category sub_category_type,
    requirement_summary text,
    requirement_statement text,
    is_kpi boolean,
	additional_data_elem text
);

CREATE INDEX pws_violations_id_idx ON pws_violations (id);

COMMENT ON TABLE pws_violations IS 'Contains QAE evaluation reports. There are two kinds of reports: shipment and counseling. You can tell them apart based on whether shipment_id is NULL.';
COMMENT ON COLUMN pws_violations.paragraph_number IS 'Paragraph number the violation relates to (1.2.3.4.5)';
COMMENT ON COLUMN pws_violations.title IS 'Paragraph title of the violation';
COMMENT ON COLUMN pws_violations.category IS 'Top level category of the violation (Pre-Move Services, Physical Move Services, etc.)';
COMMENT ON COLUMN pws_violations.sub_category IS 'Sub-category of the violation (Customer Support, Counseling, etc.)';
COMMENT ON COLUMN pws_violations.requirement_summary IS 'PWS Requirement Summary';
COMMENT ON COLUMN pws_violations.requirement_statement IS 'Requirement Statement in PWS';
COMMENT ON COLUMN pws_violations.is_kpi IS 'Whether the violation is a Key Performance Indicator (KPI)';
COMMENT ON COLUMN pws_violations.additional_data_elem IS 'Desired Additional Data Element (KPIs Only)';

